drop table if exists patients;

create table patients (
  id bigint not null auto_increment,
  title varchar(10) null,
  first_name varchar(30) null,
  last_name varchar(30) null,
  address_1 varchar(100) null,
  address_2 varchar(100) null,
  address_3 varchar(100) null,
  address_4 varchar(100) null,
  address_5 varchar(100) null,
  postcode  varchar(10) null,
  phone     varchar(20) null,
  born      date null,
  gender    varchar(10) null,
  nhs_number varchar(20) null,
  gp_name varchar(60) null,
  gp_address_1 varchar(100) null,
  gp_address_2 varchar(100) null,
  gp_address_3 varchar(100) null,
  gp_address_4 varchar(100) null,
  gp_address_5 varchar(100) null,
  gp_postcode  varchar(10) null,
  department varchar(30) null,
  primary key (id)
);



